type ClusterOptions = {
  nodes?: string[];
  port?: number | string;
  sslContext?: string;
  username?: string;
  password?: string;
}

class Cluster {
  constructor(options?: ClusterOptions);
  connect(): Session;
}

interface Session {
  executeSync(query: string): Array<string>; // This string should be a row later
}

declare namespace NodeppExampleModule {
  interface ExampleModule {
    Cluster: typeof Cluster;
  }
}

declare module '@nodepp/example' {
  const exampleModule: NodeppExampleModule.ExampleModule;
  export = exampleModule;
}
